        mst = []
        dfs(graph, visited, mst, k)
        return len(mst)

    def update_weights(edges: List[tuple], operations: int) -> List[tuple]:
        new_edges = []
        for edge in edges:
            if edge[2] > operations:
                new_edges.append((edge[0], edge[1], edge[2] - operations))
            else:
                new_edges.append((edge[0], edge[1], 1))
        return new_edges

