        return abs(point1[0] - point2[0]) + abs(point1[1] - point2[1])

    def bfs(start, level):
        visited = set()
        queue = [(start, 0)]

        while queue:
            point, distance_traveled = queue.pop(0)

            if point not in visited and max(point) <= level:
                visited.add(point)
                yield (point, distance_traveled)

                for neighbor in [(point[0] + 1, point[1]), (point[0] - 1, point[1]), (point[0], point[1] + 1), (point[0], point[1] - 1)]:
                    if neighbor in key_points:
                        queue.append((neighbor, distance_traveled + distance(point, neighbor)))

    n = int(stdin)
    key_points = [(x, y) for x, y in [tuple(map(int, input().split())) for _ in range(n)]]

    min_distance = float('inf')
    for level in range(1, n + 1):
        key_points_in_level = [point for point in key_points if max(point) == level]
        start = key_points_in_level[0]
        for _, distance_traveled in bfs(start, level):
            min_distance = min(min_distance, distance_traveled)

    return str(min_distance)

