    def can_partition(arr: list) -> bool:
        n = len(arr)
        total_sum = sum(arr)
        if total_sum % 3 != 0:
            return False
        target_sum = total_sum // 3

        dp = [False] * (n + 1)
        dp[0] = True

        for i in range(1, n + 1):
            for j in range(1, target_sum + 1):
                if arr[i - 1] <= j:
                    dp[i] |= dp[i - 1]
                if arr[i - 1] == j:
                    break

        return dp[n]

    def can_partition_into_three(arr: list) -> bool:
        n = len(arr)
        target_sum = sum(arr) // 3
        if sum(arr) % 3 != 0:
            return False

        for i in range(n):
            if get_sum(arr, i) == target_sum:
                return True

        return False

    def main(arr: list) -> bool:
        if can_partition(arr) and can_partition_into_three(arr):
            return True
        return False

