    def is_non_intersecting(l1, r1, l2, r2):
        return l1 <= l2 <= r1 or l2 <= l1 <= r2 or l1 <= r2 <= r1

    def is_inside(l1, r1, l2, r2):
        return l2 <= l1 and r1 <= r2

    def dfs(graph, visited, current_set, result):
        if current_set not in result:
            result.append(current_set)

        for node in graph:
            if not visited[node]:
                visited[node] = True
                dfs(graph, visited, current_set + [node], result)

