        graph = {}
        visited = {}

        for i in range(len(segments)):
            graph[i] = []
            visited[i] = False

        for i in range(len(segments)):
            for j in range(i + 1, len(segments)):
                if is_non_intersecting(*segments[i], *segments[j]):
                    graph[i].append(j)
                    graph[j].append(i)

        for i in range(len(segments)):
            for j in range(i + 1, len(segments)):
                if is_inside(*segments[i], *segments[j]):
                    graph[i].append(j)
                    graph[j].append(i)

        current_set = []
        dfs(graph, visited, current_set, result)

