    MOD = 10**9 + 7

    def factorial(n: int) -> int:
        result = 1
        for i in range(1, n+1):
            result = (result * i) % MOD
        return result

    def count_divisors(n: int) -> int:
        result = 0
        for i in range(1, int(n**0.5)+1):
            if n % i == 0:
                if n // i == i:
                    result += 1
                else:
                    result += 2
        return result

    N_fact = factorial(N)
    N_divisors = count_divisors(N_fact)

    return str(N_divisors)

