def solution(stdin: str) -> str:
    class Node:
        def __init__(self, name: str, attributes: dict, value: str, children: list):
            self.name = name
            self.attributes = attributes
            self.value = value
            self.children = children

    def parse_xml(xml: str) -> Node:
        stack = []
        lines = xml.split("\n")
        root = None
        for line in lines:
            line = line.strip()
            if not line:
                continue
            if line.startswith("<") and line.endswith(">"):
                tag = line[1:-1].split()
                if len(tag) == 1:
                    name, attributes = tag[0], {}
                else:
                    name, attributes = tag[0], dict(tag[1:])
                node = Node(name, attributes, "", [])
                if not stack:
                    root = node
                else:
                    stack[-1].children.append(node)
                stack.append(node)
            elif line.startswith("</"):
                tag = line[2:-1]
                if stack[-1].name != tag:
                    raise ValueError("Invalid XML")
                stack.pop()
            elif line.startswith("<?") and line.endswith("?>"):
                continue
            elif line.startswith("<![CDATA["):
                raise ValueError("CDATA is not supported")
            else:
                node = stack[-1]
                if line.startswith('"') or line.startswith("'"):
                    node.value += line.strip('"').strip("'")
                else:
                    node.value += line
        if len(stack) != 1:
            raise ValueError("Invalid XML")
        return root

    def max_level(node: Node, level: int = 0) -> int:
        max_lvl = level
        for child in node.children:
            max_lvl = max(max_lvl, max_level(child, level + 1))
        return max_lvl

    xml = stdin.read()
    root = parse_xml(xml)
    return str(max_level(root))

