    input_lines = stdin.splitlines()
    grid = [[int(x) for x in input_lines[i].split()] for i in range(4)]
    direction = int(input_lines[4])

    def merge_and_slide(grid: list) -> list:
        for i in range(4):
            for j in range(3):
                if grid[i][j] == grid[i][j + 1]:
                    grid[i][j] += grid[i][j + 1]
                    grid[i][j + 1] = 0
                elif grid[i][j] == 0:
                    grid[i][j] = grid[i][j + 1]
                    grid[i][j + 1] = 0
        for j in range(4):
            for i in range(3):
                if grid[i][j] == grid[i + 1][j]:
                    grid[i][j] += grid[i + 1][j]
                    grid[i + 1][j] = 0
                elif grid[i][j] == 0:
                    grid[i][j] = grid[i + 1][j]
                    grid[i + 1][j] = 0
        return grid

    def get_new_grid(grid: list, direction: int) -> list:
        if direction == 0:
            grid = [[grid[i][j] for j in range(4)] for i in range(4)]
        elif direction == 1:
            grid = [[grid[j][i] for j in range(4)] for i in range(4)]
        elif direction == 2:
            grid = [[grid[3 - i][3 - j] for j in range(4)] for i in range(4)]
        else:
            grid = [[grid[3 - j][i] for j in range(4)] for i in range(4)]
        return grid

    new_grid = get_new_grid(grid, direction)
    merged_grid = merge_and_slide(new_grid)
    new_grid = get_new_grid(merged_grid, 3 - direction)
    return "\n".join([" ".join([str(x) for x in row]) for row in new_grid])

