        for atom in molecule:
            if atom.isalpha():
                if atom in atom_count:
                    atom_count[atom] += 1
                else:
                    atom_count[atom] = 1
        return atom_count

    def count_molecules(input_molecule: str, output_molecule: str) -> int:
        input_count = parse_molecule(input_molecule)
        output_count = parse_molecule(output_molecule)

        for atom in input_count:
            if atom not in output_count:
                return 0

        max_count = input_count.get(input_molecule[0], 0)
        for atom in output_count:
            count = min(input_count.get(atom, 0), output_count[atom])
            max_count = min(max_count, count)

        return max_count

    max_count = count_molecules(input_molecule, output_molecule)
    return str(max_count * input_molecule_count)

