        if count == K:
            count_rest = count_ascending_sequences_with_K(numbers[K:], K)
            if count_rest > 0:
                return count_rest + 1
        return 0

    if K > n_numbers:
        return 0
    return count_ascending_sequences_with_K(numbers, K)

