    melody1 = stdin.splitlines()[1].split()
    melody2 = stdin.splitlines()[2].split()

    notes = ['C', 'C\#', 'D', 'D\#', 'E', 'F', 'F\#', 'G', 'G\#', 'A', 'A\#', 'B']
    notes_dict = {note: i for i, note in enumerate(notes)}

    def transpose(melody):
        return [notes[(notes_dict[note] + n) % 12] for note in melody]

    def retrograde(melody):
        return melody[::-1]

    def inversion(melody):
        return [notes[(12 - notes_dict[note]) % 12] for note in melody]

    if transpose(melody1) == melody2:
        return 'Transposition'
    elif retrograde(melody1) == melody2:
        return 'Retrograde'
    elif inversion(melody1) == melody2:
        return 'Inversion'
    else:
        return 'Nonsense'


