    M, N = map(int, stdin.split()[0:2])
    input_matrix = [list(map(int, stdin.split()[2:])) for _ in range(M)]

    # Find the shortest ladder length to reach the south east corner
    def find_shortest_ladder_length(start_row, start_col, end_row, end_col):
        if start_row == end_row and start_col == end_col:
            return 0

        if start_row < end_row:
            # Climb up to the next row
            next_row = start_row + 1
            next_col = start_col

            # Check if the next row has a shorter path
            if input_matrix[next_row][next_col] <= input_matrix[end_row][end_col]:
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col)
            else:
                # Jump down to the next row and column
                next_row = start_row
                next_col = start_col + 1
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col) + 1

        if start_row > end_row:
            # Climb down to the previous row
            next_row = start_row - 1
            next_col = start_col

            # Check if the previous row has a shorter path
            if input_matrix[next_row][next_col] <= input_matrix[end_row][end_col]:
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col)
            else:
                # Jump down to the previous row and column
                next_row = start_row
                next_col = start_col - 1
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col) + 1

        if start_col < end_col:
            # Climb right to the next column
            next_row = start_row
            next_col = start_col + 1

            # Check if the next column has a shorter path
            if input_matrix[next_row][next_col] <= input_matrix[end_row][end_col]:
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col)
            else:
                # Jump down to the next row and column
                next_row = start_row + 1
                next_col = start_col
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col) + 1

        if start_col > end_col:
            # Climb left to the previous column
            next_row = start_row
            next_col = start_col - 1

            # Check if the previous column has a shorter path
            if input_matrix[next_row][next_col] <= input_matrix[end_row][end_col]:
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col)
            else:
                # Jump down to the previous row and column
                next_row = start_row - 1
                next_col = start_col
                return find_shortest_ladder_length(next_row, next_col, end_row, end_col) + 1

    shortest_ladder_length = float('inf')
    for i in range(M):
        for j in range(N):
            if input_matrix[i][j] == 0:
                continue
            if (i, j) == (M - 1, N - 1):
                continue
            current_ladder_length = find_shortest_ladder_length(i, j, M - 1, N - 1)
            shortest_ladder_length = min(shortest_ladder_length, current_ladder_length)

    return str(shortest_ladder_length)

