    def is_safe(x, y):
        if 0 <= x < N and 0 <= y < M and grid[x][y] == '0':
            return True
        return False

    def bfs(start_x, start_y):
        queue = [(start_x, start_y, 0)]
        visited = set()
        while queue:
            x, y, steps = queue.pop(0)
            if (x, y) == (N - 1, M - 1):
                return steps
            if (x, y) not in visited:
                visited.add((x, y))
                for direction, (dx, dy) in directions.items():
                    if is_safe(x + dx, y + dy):
                        queue.append((x + dx, y + dy, steps + 1))
        return -1

    start_x, start_y = -1, -1
    for i in range(N):
        if 'S' in grid[i]:
            start_x = i
            start_y = grid[i].index('S')
            break

    if start_x == -1:
        return "NOT POSSIBLE"

    steps = bfs(start_x, start_y)
    if steps == -1:
        return "NOT POSSIBLE"
