def solution(stdin: str) -> str:
    b, d, c, l = map(int, stdin.split())

    # Check if there are any possible solutions
    if l < b + d + c:
        return "impossible"

    # Calculate the number of legs for each animal
    t = l - b - d - c

    # Find all possible combinations of animals
    def find_combinations(t, b, d, c):
        combinations = []
        for i in range(t // 3 + 1):
            for j in range(t - 3 * i):
                combinations.append((i, j, t - i - j))
        return combinations

    # Filter combinations that violate the given constraints
    def filter_combinations(combinations, b, d, c):
        return [(i, j, k) for i, j, k in combinations if 0 <= i <= b and 0 <= j <= d and 0 <= k <= c]

    # Sort combinations lexicographically
    def sort_combinations(combinations):
        return sorted(combinations, key=lambda x: (x[0], x[1], x[2]))

    # Generate all possible solutions
    def generate_solutions(combinations):
        solutions = []
        for i, j, k in combinations:
            solutions.append("{} {} {}".format(i, j, k))
        return solutions

    combinations = find_combinations(t, b, d, c)
    filtered_combinations = filter_combinations(combinations, b, d, c)
    sorted_combinations = sort_combinations(filtered_combinations)
    solutions = generate_solutions(sorted_combinations)

    return "\n".join(solutions)

