                        if distances[i+1][l] > distances[i][j] + abs(j-i):
                            distances[i+1][l] = distances[i][j] + abs(j-i)
                    # move left
                    if j > 0:
                        if distances[i][j-1] > distances[i][j] + abs(j-i):
                            distances[i][j-1] = distances[i][j] + abs(j-i)
