        if move.isdigit():
            # Add the move to the undo_moves list
            undo_moves.append(move)
        else:
            m = int(move.split()[1])
            # If the move is undo, remove the last m moves from undo_moves
            if m > len(undo_moves):
                m = len(undo_moves)
            undo_moves = undo_moves[:-m]

    # Apply the undo moves to the current_child
    for move in undo_moves:
        if move.isdigit():
            p = int(move)
            # If p is positive, throw the egg to the child clockwise
            if p > 0:
                current_child = (current_child + p) % n
            # If p is negative, throw the egg to the child counter-clockwise
            elif p < 0:
                current_child = (current_child - p) % n

