        global min_steps
        if min_steps == 0:
            return
        if pos == (1, 1):
            min_steps = min(min_steps, steps)
            return
        if (pos[0], pos[1]) in blocked_cells:
            return

        valid_moves = [(pos[0]+2, pos[1]+1), (pos[0]+2, pos[1]-1),
                        (pos[0]-2, pos[1]+1), (pos[0]-2, pos[1]-1),
                        (pos[0]+1, pos[1]+2), (pos[0]+1, pos[1]-2),
                        (pos[0]-1, pos[1]+2), (pos[0]-1, pos[1]-2)]

        for move in valid_moves:
            if is_valid(move):
                find_min_steps(move, steps+1)

    find_min_steps(knight_pos, 0)

    if min_steps == float('inf'):
        return "-1"
    else:
        return min_steps

