def smoothclamp(x, x_min, x_max):
    while x < x_min:
        x += (x_max - x_min)
    while x > x_max:
        x -= (x_max - x_min)
    return x

def smoothclamp(x, min, max, N):
    if N == 0:
        return min if x < min else max if x > max else x

    if x < min:
        return smoothclamp(x + (min - x) / 2 / N, min, max, N - 1)
    elif x > max:
        return smoothclamp(x - (x - max) / 2 / N, min, max, N - 1)
    else:
        return x

